import paho.mqtt.client as mqttvar_tau_thorben = 512.0var_tau_livingroom = 512.0var_tau_outdoor = 512.0var_tau_johanna = 512.0var_tau_parents = 512.0def on_message(client, userdata, msg):   print(msg.topic+" "+str(msg.payload))      if msg.topic == "tau_thorben":      var_tau_thorben = msg.payload   if msg.topic == "tau_livingroom":      var_tau_livingroom = msg.payload	     if msg.topic == "tau_outdoor":      var_tau_outdoor = msg.payload      if msg.topic == "tau_johanna":      var_tau_johanna = msg.payload	     if msg.topic == "tau_parents":      var_tau_parents = msg.payload        print("tau_thorben: " + str(var_tau_thorben))   print("tau_livingroom: " + str(var_tau_livingroom))   print("tau_putdoor: " + str(var_tau_outdoor))   print("tau_johanna: " + str(var_tau_johanna))   print("tau_parents: " + str(var_tau_parents))   		def on_connect(client, userdata, flags, rc):    print("Connected with result code "+str(rc))	client = mqtt.Client()client.on_connect = on_connectclient.on_message = on_messageclient.connect("192.168.178.102")client.subscribe("tau_thorben", 2)client.subscribe("tau_livingroom", 2)client.subscribe("tau_outdoor", 2)client.subscribe("tau_johanna", 2)client.subscribe("tau_parents", 2)client.publish("kai_uwe","Empfehlung")client.loop_forever()